/// <summary>
/// Calculates the nth Fibonacci number using an iterative approach.
/// </summary>
/// <param name="n">The index in the Fibonacci sequence.</param>
/// <returns>The nth Fibonacci number.</returns>
public static int CalculateFibonacci(int n)
{
    if (n <= 1)
    {
        return n;
    }

    int a = 0;
    int b = 1;

    for (int i = 2; i <= n; i++)
    {
        int temp = a + b;
        a = b;
        b = temp;
    }

    return b;
}
