using System;

/// <summary>
/// Defines the severity levels for logging.
/// </summary>
public enum LogLevel
{
    Debug,
    Info,
    Warning,
    Error
}

/// <summary>
/// A placeholder for a global configuration class.
/// In a real application, this might be populated from a config file.
/// </summary>
public class AppConfig
{
    public bool DebugEnabled { get; set; }
}

/// <summary>
/// A static class to hold the global configuration instance,
/// mimicking the global 'global_config' variable in the C code.
/// </summary>
public static class GlobalState
{
    public static AppConfig GlobalConfig { get; set; }
}

/// <summary>
/// Provides logging functionality.
/// </summary>
public static class Logger
{
    // C# equivalent of the string literal array.
    // Using ToString() on the enum is often more idiomatic, but this is a direct conversion.
    private static readonly string[] s_levelStr = { "DEBUG", "INFO", "WARNING", "ERROR" };

    /// <summary>
    /// Logs a message to the console if debugging is enabled.
    /// </summary>
    /// <param name="level">The severity level of the message.</param>
    /// <param name="message">The message content to log.</param>
    public static void Log(LogLevel level, string message)
    {
        // Equivalent to C's 'if (global_config && global_config->debug_enabled)'
        if (GlobalState.GlobalConfig != null && GlobalState.GlobalConfig.DebugEnabled)
        {
            // Use Console.WriteLine with string interpolation, the C# equivalent of printf.
            // We cast the enum to an int to use it as an array index.
            Console.WriteLine($"[{s_levelStr[(int)level]}] {message}");
        }
    }
}
